(9, 1, '2020-04-23 07:43:20', '2020-04-23 07:43:20', '<!-- wp:acf/resources {\n    \"id\": \"block_5eb3b20a245c0\",\n    \"name\": \"acf\\/resources\",\n    \"data\": {\n        \"h2title\": \"Resources\",\n        \"_h2title\": \"field_5eb3f54e1c1a0\",\n        \"region_0_title\": \"National\",\n        \"_region_0_title\": \"field_5eb3ae7556786\",\n        \"region_0_content\": \"<span style=\\\"font-weight: 400;\\\">National Minority AIDS Council\\r\\n<\\/span><a href=\\\"http:\\/\\/nmac.org\\/\\\"><span style=\\\"font-weight: 400;\\\">Nmac.org<\\/span><\\/a>\\r\\n\\r\\n<span style=\\\"font-weight: 400;\\\">The American Foundation for AIDS Research\\r\\n<\\/span><a href=\\\"http:\\/\\/amfar.org\\/\\\"><span style=\\\"font-weight: 400;\\\">Amfar.org<\\/span><\\/a>\\r\\n\\r\\n<span style=\\\"font-weight: 400;\\\">Center for Disease Control\\r\\n<\\/span><a href=\\\"http:\\/\\/cdc.gov\\/\\\"><span style=\\\"font-weight: 400;\\\">CDC.gov<\\/span><\\/a>\\r\\n\\r\\n<span style=\\\"font-weight: 400;\\\">AIDS United\\r\\n<\\/span><a href=\\\"http:\\/\\/aidsunited.org\\/\\\"><span style=\\\"font-weight: 400;\\\">aidsunited.org<\\/span><\\/a>\\r\\n\\r\\n<span style=\\\"font-weight: 400;\\\">APLA Health\\r\\n<\\/span><a href=\\\"http:\\/\\/aplahealth.org\\/\\\"><span style=\\\"font-weight: 400;\\\">Aplahealth.org<\\/span><\\/a>\",\n        \"_region_0_content\": \"field_5eb3ae7d56787\",\n        \"region_1_title\": \"Baltimore, MD\",\n        \"_region_1_title\": \"field_5eb3ae7556786\",\n        \"region_1_content\": \"<span style=\\\"font-weight: 400;\\\">Community Education Group <\\/span>\\r\\n\\r\\n<span style=\\\"font-weight: 400;\\\">African Ensemble Theater <\\/span>\\r\\n\\r\\n<span style=\\\"font-weight: 400;\\\">Johns Hopkins School of Public Health\\r\\n<\\/span><a href=\\\"https:\\/\\/www.jhsph.edu\\/\\\"><span style=\\\"font-weight: 400;\\\">https:\\/\\/www.jhsph.edu<\\/span><\\/a>\\r\\n\\r\\n<span style=\\\"font-weight: 400;\\\">Gay Men\'s Health Collaborativ